INSERT INTO `_OTG_options` VALUES("171", "theme_mods_orfeo", "a:22:{s:18:\"nav_menu_locations\";a:0:{}s:30:\"hestia_alternative_blog_layout\";s:18:\"blog_normal_layout\";s:31:\"hestia_alternative_footer_style\";s:12:\"black_footer\";s:18:\"custom_css_post_id\";i:-1;s:16:\"hestia_blog_hide\";b:1;s:11:\"custom_logo\";i:8;s:27:\"hestia_big_title_background\";s:84:\"http://box5639.temp.domains/~rcfhlcom/wp-content/uploads/2018/04/bg_home_hero_v2.jpg\";s:22:\"hestia_big_title_title\";s:35:\"Welcome to River City Floor Hockey!\";s:23:\"hestia_slider_alignment\";s:6:\"center\";s:28:\"hestia_big_title_button_text\";s:27:\"Stay Updated on league news\";s:34:\"orfeo_big_title_second_button_text\";s:0:\"\";s:34:\"orfeo_big_title_second_button_link\";s:0:\"\";s:21:\"hestia_big_title_text\";s:92:\"We are the newest league to come to the St. Louis area and we are ready to elevate the game.\";s:24:\"hestia_feature_thumbnail\";s:0:\"\";s:25:\"hestia_contact_background\";s:105:\"http://box5639.temp.domains/~rcfhlcom/wp-content/uploads/2018/04/Screen-Shot-2018-04-17-at-1.04.33-AM.png\";s:20:\"hestia_contact_title\";s:30:\"Stay up to date on league news\";s:23:\"hestia_contact_subtitle\";s:240:\"We would love to hear from you. We are collecting a list of players and captains interested in our inaugural season. Whatever your reason for contacting just let us know in the message and we\'ll be sure to serve you accordingly. Thank you! \";s:26:\"hestia_contact_content_new\";s:463:\"<div class=\"hestia-info info info-horizontal\"><div class=\"description\"><p></p></div></div><div class=\"hestia-info info info-horizontal\"><div class=\"icon icon-primary\"><i class=\"fa fa-mobile\"></i></div><div class=\"description\"><h4 class=\"info-title\">Give us a ring or text</h4><p>If you would rather talk to one of us directly, don\'t hesitate to call or text.</p><p>Sam Casey<br />(314) 956-7534</p><p>Brad Callahan<br /><span>(636) 219-7103</span></p></div></div>\";s:28:\"hestia_big_title_button_link\";s:8:\"#contact\";s:18:\"hestia_page_editor\";s:805:\"<h5>About River City Floor Hockey</h5><p><strong>Rink Made for Hockey<br /></strong>Unlike some of the other leagues in town we play on a roller rink optimized for playing hockey. The surface has plenty of traction and is smooth and durable, the corners are rounded, there are proper benches, and the boards go all the way around the playing area.</p><p><strong>Dedicated to Creating an Elite League Experience</strong><br />We want our players to feel like they have a voice. As an organization we are always open to feedback and will do what is necessary to make improvements.</p><p><strong>Stats and Video</strong><br />We want to continue to engage with our players even after they have left the rink. We plan to record stats and video for every game that will be posted every week on our website.</p>\";s:23:\"hestia_header_alignment\";s:4:\"left\";s:16:\"sidebars_widgets\";a:2:{s:4:\"time\";i:1525647822;s:4:\"data\";a:10:{s:19:\"wp_inactive_widgets\";a:6:{i:0;s:8:\"search-2\";i:1;s:14:\"recent-posts-2\";i:2;s:17:\"recent-comments-2\";i:3;s:10:\"archives-2\";i:4;s:12:\"categories-2\";i:5;s:6:\"meta-2\";}s:9:\"sidebar-1\";a:0:{}s:17:\"subscribe-widgets\";a:0:{}s:22:\"blog-subscribe-widgets\";a:0:{}s:19:\"sidebar-woocommerce\";a:0:{}s:15:\"sidebar-top-bar\";a:0:{}s:14:\"header-sidebar\";a:0:{}s:18:\"footer-one-widgets\";a:0:{}s:18:\"footer-two-widgets\";a:0:{}s:20:\"footer-three-widgets\";a:0:{}}}}", "yes");
