INSERT INTO `_OTG_posts` VALUES("20", "1", "2018-04-17 06:19:11", "2018-04-17 06:19:11", "{\n    \"orfeo::hestia_contact_background\": {\n        \"value\": \"http://box5639.temp.domains/~rcfhlcom/wp-content/uploads/2018/04/Screen-Shot-2018-04-17-at-1.04.33-AM.png\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2018-04-17 06:04:50\"\n    },\n    \"orfeo::hestia_page_editor\": {\n        \"value\": \"<h5>About River City Floor Hockey</h5><p><strong>Rink Made for Hockey<br /></strong>Unlike some of the other leagues in town we play on a roller rink optimized for playing hockey. The surface has plenty of traction and is smooth and durable, the corners are rounded, there are proper benches, and the boards go all the way around the playing area.</p><p><strong>Dedicated to Creating an Elite League Experience</strong><br />We want our players to feel like they have a voice. As an organization we are always open to feedback and will do what is necessary to make improvements.</p><p><strong>Stats and Video</strong><br />We want to continue to engage with our players even after they have left the rink. We plan to record stats and video for every game that will be posted every week on our website.</p>\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2018-04-17 06:18:41\"\n    }\n}", "", "", "trash", "closed", "closed", "", "2d58968d-9a8c-452c-b465-4a6882e85f3c", "", "", "2018-04-17 06:19:11", "2018-04-17 06:19:11", "", "0", "http://box5639.temp.domains/~rcfhlcom/?p=20", "0", "customize_changeset", "", "0");
